mapper.select:
SELECT po.id, po.uid as uid, po.pay_order_id AS payOrderId, po.channel_order_id AS channelOrderId, po.amount, po.currency_amount AS currencyAmount, po.fee, po.trade_value AS tradeValue, po.sett_value AS settValue, po.currency, po. STATUS, po.pay_passage_id AS payPassageId, po.client_ip AS clientIp, po.create_time AS createTime, po.update_time AS updateTime, po.pay_succ_time AS paySuccTime, po.ip_address AS ipAddress, po.error_msg AS errorMsg, IFNULL(bu.phone, bu.email) AS username, pp.passage_name AS payPassageName FROM bg_pay_order po LEFT JOIN bg_user bu ON po.uid = bu.uid LEFT JOIN bg_pay_passage pp ON po.pay_passage_id = pp.id
and po.pay_order_id = #{payOrderId} and po.currency = #{currency} and po.status = #{status} and po.uid = #{uid} and (bu.phone like concat('%',#{username},'%') or bu.email like concat('%',#{username},'%')) and pp.payPassageName like concat('%',#{payPassageName},'%')
and po.create_time >= #{beginTime}
and po.create_time <= #{endTime}
order by po.create_time desc

mapper.update:
update bg_pay_order
pay_order_id = #{payOrderId}, channel_order_id = #{channelOrderId}, amount = #{amount}, fee = #{fee}, currency_amount = #{currencyAmount}, currency = #{currency}, sett_value = #{settValue}, trade_value = #{tradeValue}, status = #{status}, pay_passage_id = #{payPassageId}, client_ip = #{clientIp}, ip_address = #{ipAddress}, create_time = #{createTime}, update_time = #{updateTime}, pay_succ_time = #{paySuccTime}, error_msg = #{errorMsg},
where id = #{id}

update bg_pay_order
channel_order_id = #{channelOrderId}, amount = #{amount}, fee = #{fee}, currency_amount = #{currencyAmount}, currency = #{currency}, sett_value = #{settValue}, trade_value = #{tradeValue}, status = #{status}, pay_passage_id = #{payPassageId}, client_ip = #{clientIp}, ip_address = #{ipAddress}, create_time = #{createTime}, update_time = #{updateTime}, pay_succ_time = #{paySuccTime}, error_msg = #{errorMsg},
where pay_order_id = #{payOrderId}
